import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators, ReactiveFormsModule, FormsModule } from '@angular/forms';
import { Router } from '@angular/router';
import { MatSnackBar, MatSnackBarModule } from '@angular/material/snack-bar';
import { AuthService } from '../../../core/auth/auth.service';
import { CommonModule } from '@angular/common';
import { MatFormFieldModule } from '@angular/material/form-field';
import { MatInputModule } from '@angular/material/input';
import { MatButtonModule } from '@angular/material/button';
import { MatIconModule } from '@angular/material/icon';
import { MatCardModule } from '@angular/material/card';
import { MatProgressSpinnerModule } from '@angular/material/progress-spinner';
import { MatDividerModule } from '@angular/material/divider';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.scss'],
  standalone: true,
  imports: [
    CommonModule,
    ReactiveFormsModule,
    FormsModule,
    MatFormFieldModule,
    MatInputModule,
    MatButtonModule,
    MatIconModule,
    MatCardModule,
    MatSnackBarModule,
    MatProgressSpinnerModule,
    MatDividerModule
  ]
})
export class LoginComponent implements OnInit {
  loginForm!: FormGroup;
  loading = false;
  hidePassword = true;

  constructor(
    private formBuilder: FormBuilder,
    private authService: AuthService,
    private router: Router,
    private snackBar: MatSnackBar
  ) { }

  ngOnInit(): void {
    // Check if already logged in
    if (this.authService.isAuthenticated()) {
      this.router.navigate(['/']);
      return;
    }

    // Initialize form
    this.loginForm = this.formBuilder.group({
      usernameOrEmail: ['', [Validators.required]],
      password: ['', [Validators.required, Validators.minLength(6)]]
    });
  }

  onSubmit(): void {
    if (this.loginForm.invalid) {
      return;
    }

    // Using the exact format from the API documentation
    const loginPayload = {
      usernameOrEmail: this.loginForm.value.usernameOrEmail,
      password: this.loginForm.value.password
    };

    this.loading = true;
    this.authService.login(loginPayload)
      .subscribe({
        next: () => {
          this.router.navigate(['/']);
        },
        error: (error) => {
          this.snackBar.open(error.message || 'Login failed', 'Close', {
            duration: 5000,
            panelClass: ['error-snackbar']
          });
          this.loading = false;
        }
      });
  }

  navigateToRegister(): void {
    this.router.navigate(['/auth/register']);
  }
}
<div class="auth-container">
  <div class="auth-form-side">
    <div class="auth-form-wrapper">
      <div class="auth-header">
        <div class="logo">
          <div class="logo-icon">SC</div>
          <h1 class="logo-text">Speekly Connect</h1>
        </div>
        <p class="subtitle">Admin Dashboard Login</p>
      </div>

      <mat-card class="auth-card">
        <mat-card-content>
          <form [formGroup]="loginForm" (ngSubmit)="onSubmit()">
            <mat-form-field appearance="outline">
              <mat-label>Username or Email</mat-label>
              <input 
                matInput 
                formControlName="usernameOrEmail" 
                placeholder="Enter your username or email"
                autocomplete="username"
              >
              <mat-icon matPrefix>person</mat-icon>
              <mat-error *ngIf="loginForm.get('usernameOrEmail')?.hasError('required')">
                Username or email is required
              </mat-error>
            </mat-form-field>

            <mat-form-field appearance="outline">
              <mat-label>Password</mat-label>
              <input 
                matInput 
                [type]="hidePassword ? 'password' : 'text'" 
                formControlName="password"
                placeholder="Enter your password"
                autocomplete="current-password"
              >
              <mat-icon matPrefix>lock</mat-icon>
              <button 
                mat-icon-button 
                matSuffix 
                (click)="hidePassword = !hidePassword" 
                [attr.aria-label]="'Hide password'" 
                [attr.aria-pressed]="hidePassword"
              >
                <mat-icon>{{hidePassword ? 'visibility_off' : 'visibility'}}</mat-icon>
              </button>
              <mat-error *ngIf="loginForm.get('password')?.hasError('required')">
                Password is required
              </mat-error>
              <mat-error *ngIf="loginForm.get('password')?.hasError('minlength')">
                Password must be at least 6 characters
              </mat-error>
            </mat-form-field>

            <div class="form-actions">
              <button 
                mat-raised-button 
                color="primary" 
                type="submit" 
                class="full-width"
                [disabled]="loginForm.invalid || loading"
              >
                <mat-spinner *ngIf="loading" diameter="20" class="button-spinner"></mat-spinner>
                <span *ngIf="!loading">Log In</span>
              </button>
            </div>
          </form>
        </mat-card-content>
        <mat-card-actions>

        </mat-card-actions>
      </mat-card>
    </div>
  </div>

  <div class="auth-hero-side">
    <div class="hero-content">
      <h2>Welcome to Speekly Connect Admin</h2>
      <p>Manage your social media platform with powerful tools for content moderation, user management, and analytics.</p>
      <div class="hero-features">
        <div class="feature-item">
          <mat-icon>people</mat-icon>
          <span>User Management</span>
        </div>
        <div class="feature-item">
          <mat-icon>comment</mat-icon>
          <span>Content Moderation</span>
        </div>
        <div class="feature-item">
          <mat-icon>bar_chart</mat-icon>
          <span>Analytics & Reports</span>
        </div>
      </div>
    </div>
  </div>
</div>
