import { Component, Input, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { MatSidenav } from '@angular/material/sidenav';
import { MatToolbarModule } from '@angular/material/toolbar';
import { MatIconModule } from '@angular/material/icon';
import { MatButtonModule } from '@angular/material/button';
import { MatMenuModule } from '@angular/material/menu';
import { MatDividerModule } from '@angular/material/divider';
import { MatBadgeModule } from '@angular/material/badge';
import { AuthService } from '../../core/auth/auth.service';
import { UserProfile } from '../../core/models/user.model';

@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.scss'],
  standalone: true,
  imports: [
    CommonModule,
    MatToolbarModule,
    MatIconModule,
    MatButtonModule,
    MatMenuModule,
    MatDividerModule,
    MatBadgeModule
  ]
})
export class HeaderComponent implements OnInit {
  @Input() sidenavRef!: MatSidenav;
  @Input() isMobile = false;

  currentUser: UserProfile | null = null;

  constructor(private authService: AuthService) { }

  ngOnInit(): void {
    this.authService.currentUser.subscribe(user => {
      this.currentUser = user;
    });
  }

  toggleSidenav(): void {
    this.sidenavRef.toggle();
  }

  logout(): void {
    this.authService.logout();
  }
}
<mat-toolbar color="primary" class="header-toolbar">
  <button 
    mat-icon-button 
    class="menu-button" 
    aria-label="Toggle sidebar"
    (click)="toggleSidenav()" 
    *ngIf="isMobile">
    <mat-icon>menu</mat-icon>
  </button>

  <span class="header-title">
    <span class="title-main">Speekly Connect</span>
    <span class="title-separator">|</span>
    <span class="title-secondary">Admin</span>
  </span>

  <span class="spacer"></span>

  <div class="header-actions">
    <button mat-button [matMenuTriggerFor]="userMenu" class="user-profile-button">
      <div class="avatar" *ngIf="!currentUser?.avatarUrl">
        {{ (currentUser?.username || 'A').charAt(0).toUpperCase() }}
      </div>
      <img 
        *ngIf="currentUser?.avatarUrl" 
        [src]="currentUser?.avatarUrl || ''" 
        alt="Profile picture"
        class="avatar-img" 
      />
      <span class="username">{{ currentUser?.username || 'Admin' }}</span>
      <mat-icon>arrow_drop_down</mat-icon>
    </button>

    <mat-menu #userMenu="matMenu" xPosition="before">
      <button mat-menu-item (click)="logout()">
        <mat-icon>exit_to_app</mat-icon>
        <span>Logout</span>
      </button>
    </mat-menu>
  </div>
</mat-toolbar>
